renderpass.code:
#define AA 1 // this for further imp.

const float PI = 3.14159265;
const int iter = 123;
const float zz = 3.0;
const float h = 0.1;

vec2 complex_power(vec2 z, float x) {
    float r = length(z);
    float theta = atan(z.y, z.x);
    float r_pow = pow(r, x);
    float x_theta = x * theta;
    return r_pow * vec2(cos(x_theta), sin(x_theta));
}

vec2 f(vec2 z) {
    return complex_power(z, 1.5) - vec2(0.2, 0.0);
}

vec2 implicit(vec2 z) {
    vec2 dz = vec2(h, 0.0);
    int i;
    for (i = 0; i < iter; i++) {
        dz = 1.5 * pow(length(z), 0.5) * dz;
        z = f(z);
        if (dot(z, z) > zz) break;
    }
    return vec2(float(i), dot(z, z) / dot(dz, dz));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 col = vec3(0.0);
    vec2 pan = vec2(1.0, 2.08);
    float zoom = 0.24;
    for(int m = 0; m < AA; m++)
    for(int n = 0; n < AA; n++) {
        vec2 uv = ((fragCoord - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x) + pan) * zoom;
        vec2 z_and_i = implicit(uv);
        float iter_ratio = z_and_i.x / float(iter);
        float sharpness = z_and_i.y;
        vec3 col1 = 0.5 + 0.5 * cos(1.0 + iTime + vec3(0.0, 0.5, 1.0) + PI * vec3(2.0 * sharpness));
        vec3 col2 = 0.5 + 0.5 * cos(4.1 + iTime + PI * vec3(sharpness));
        col += mix(col1, col2, iter_ratio);
    }
    col = sqrt(col / float(AA*AA)); 
    fragColor = vec4(col, 1.0);
}
