void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 normalizedCoord = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
for(float i = 0.0; i< 19.0; i+=1.0){
 normalizedCoord =abs(normalizedCoord );
 normalizedCoord -=0.6;
  normalizedCoord *=1.3;
  normalizedCoord *= mat2(
  cos(0.2),-sin(0.2),
  sin(0.2),cos(0.2)
  );
}
    normalizedCoord *= mat2(
 sin(0.3),cos(0.3),
  cos(0.3),sin(0.3)
  );
    fragColor = vec4(0,length(normalizedCoord),length(normalizedCoord),1.0);
}
