float rand(float seed){

    return fract(sin(seed * 10000.0));

}
void mainImage(out vec4 outColor, vec2 fragCoord)
{

    outColor *= 0.;
    vec2 st = fragCoord.xy / iResolution.y;


    vec2 b = vec2(0,.2), p;

    mat2 rotation;

    if (iTime > 3.0 && iTime < 3.5)
        st.x += rand(ceil(iTime * 8.0 * st.y * 9.)) * (rand(ceil(iTime * 400.0))<0.3?0.0:1.0);
    float bitch = iTime * (cos(iTime) + 1.0 )* 0.3 * (iTime <5.0?1.0:0.0);


    for(float i=.9; i<21.0 ;++i){

        rotation = mat2(cos(i + vec4(0,33,11,0)));

        st = st * (i) * .1 * (min(iTime,11.5) * 0.06 + 0.62);

        st.y += iTime * (0.18) + bitch - 0.1;

        st = rotation * (fract((st)*rotation)-.5);
        outColor += 0.001/ length(clamp(st,-b,b)-st);

    }
}
