void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float baseScale = 0.1;
    const float firstBand = 0.08;
    const float bandScale = 0.8;
    float band = floor(log((iResolution.y - fragCoord.y) / firstBand) / log(1.0 + bandScale));
    float scale = baseScale * pow(bandScale, band);
    float xCoord = floor((fragCoord.x - (iResolution.x / 2.0)) * scale);
    float yCoord = floor((fragCoord.y - (iResolution.y / 2.0)) * scale);
    float red = mod(yCoord, 8.0) / 8.0;
    float blue = mod(xCoord, 8.0) / 8.0;
    float modulatedCoord = xCoord + yCoord;
    bool bw = mod(modulatedCoord, 2.0) == 0.0;

    // Time varying pixel color
    vec3 col = bw ? vec3(1.0,1.0,1.0) : vec3(red,0.0,blue);

    // Output to screen
    fragColor = vec4(col,1.0);
}
