void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 float Time = sin(iTime) * 2.0 +1.0;
 vec2 Coords = fragCoord.xy / iResolution.xy;

 Coords = Coords * Time;

 //vec4 Color = vec4(Coords.x, Coords.y, 0.0, 1.0);

 vec3 Color0 = vec3(Coords.x * 0.9, Coords.y * 0.4, 0.5);
 vec3 Color1 = vec3(Coords.x, Coords.y, 0.8);

 vec3 ColorF;

   if(Time < 1.0)
 {
     ColorF = Color0;
 }
 else
 {
     ColorF = Color1;
 }


  fragColor = vec4(ColorF, 0.8);
 }
