#define time iTime
#define res iResolution.xy

vec3 p(float t) {
    vec3 a = vec3(0., 0., .5);
    vec3 b = vec3(.8, .5, .5);
    vec3 c = vec3(.5, 1., .2);
    vec3 d = vec3(0.263, 0.416, 0.557);
    return a + b * cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/res;
    st.x *= res.x/res.y;
    float off = time*.065;
    float sz =5.;
    st -= off;
    st = fract(st*sz);
    st = st *2.-1.;    
    float g = 3.;
    float d = length(min(abs(st)-.3, 0.))*g;
    vec3 c = p(d);
    fragColor = vec4(c,1.0);
}
