void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    float d = length(uv);
    float t = pow(smoothstep(0.9, 0.2, d),0.35);

    // For rainbow : color = 
    vec3 rainbow = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    // For static color : color = 
    vec3 idle = vec3(1.0,1.0,1.0);


    vec3 color = vec3(rainbow);

    vec4 colorFinal = vec4(color.rgb,1.0);

    d = sin(d*8.0-iTime);
    d = abs(d);
    d = 0.2/d;
    colorFinal *= d*t;

    // Output to screen
    fragColor = vec4(colorFinal);
}
