vec3 random(vec3 n)
{
  return fract(sin(dot(n, vec3(127.1, 311.7, 157.3))) * vec3(43758.5453123, 5453123.43758, 76975.1234567));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 uv = vec3(fragCoord/iResolution.xy * vec2(iResolution.x / iResolution.y, 1.0), iTime * 0.05);
    uv *= 15.0;

    vec3 uv_floor = floor(uv);
    vec3 uv_fract = fract(uv);
    float sum = 1000.0;

    for(int i = -1; i <= 1; i++)
    {
        for(int j = -1; j <= 1; j++)
        {
            for(int k = -1; k <= 1; k++)
            {
                vec3 index = vec3(i,j,k);
                float dst = distance(uv_fract, random(uv_floor + index) + index);
                sum = min(dst, sum);
            }
        }
    }

    fragColor = vec4(vec3(sum),1.0);
}
