#define lineThickness 0.001
#define PI     3.14159265
#define TWO_PI 6.28318530

float pingPong(float scale, float value)
{
    float m =  mod(abs(value), 2. * scale);
    return scale - abs(m-scale);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float circular = length(uv);
    float circles = pingPong(.05, circular);
    circles = lineThickness / circles;

    float cross = lineThickness / min(abs(uv.x), abs(uv.y));

    float circleMask = step(circular, 0.4);
    cross *= circleMask;

    float radar = circles + cross;

    float radial = atan(uv.x, uv.y) + PI/2.;
    radial = pow(fract(radial/TWO_PI - iTime * .2), 10.);
    radial *= circleMask;
    radial = clamp(radial, 0., 1.);

    vec3 col = radial * vec3(.8, .9, .1);
    col = mix(col, vec3(.1, .9, .1), radar);

    col *= smoothstep(.42, .4, circular);

    fragColor = vec4(col,1.0);
}
