void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    //vec2 u = fract(uv * 10.0);
    //float g = min(u.x,u.y);
    vec2 f = fragCoord.xy * .005;
    vec2 m = fract(f);
    vec2 i = floor(f);
    float k = ceil(min(m.x, m.y) - .004);

    float z = step(i.x, 1.) * step(i.y, .0);
    k -= z;


    vec2 r = fract(m * 8.);


    vec2 id = vec2(3.0, 7.0);
    vec2 ii = floor(r * 8.0);

    float h = step(id.x, ii.x) * step(id.y, ii.y);

    float s = ceil(min(r.x, r.y) - .04);

    float grid = 1.0 - k - s;
    //grid = max(grid,h);
    fragColor = vec4(grid);
}
