void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor.rgb = col;
}

//https://www.shadertoy.com/view/3tdSDj
float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if(iFrame == 0) return;

    vec2 uv = fragCoord/iResolution.xy;

    vec4 mouseOld = texture(iChannel1, uv);
    vec4 mouseNew = iMouse;


    bool isDrawing = mouseNew.z > 0.0;
    bool isNewLine = mouseNew.w > 0.0;


    if(!isDrawing)
    {
        fragColor = texture(iChannel0, uv);  
        return;
    }


    vec2 samplePoint = fragCoord / iResolution.x;

    vec2 lineStart = (isNewLine? abs(mouseNew.zw) : mouseOld.xy) / iResolution.x;
    vec2 lineEnd = mouseNew.xy / iResolution.x;


    float distanceToLine = udSegment(samplePoint, lineStart, lineEnd);


    if(distanceToLine <= 0.025)
    {
        fragColor = vec4(sin(iTime), cos(iTime), 1.0, 1.0); 
        return;
    }

    fragColor = texture(iChannel0, uv);  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = iMouse;
}
