float flower(vec2 position, float radius) {
    vec2 s = vec2(sqrt(3.0), 3.0) * radius;
    position = (fract(position / s) - 0.5) * s;
    position = abs(position);
    position -= 2.0 * min(dot(position - vec2(sqrt(3.0) * 0.5 * radius, 0.5 * radius), vec2(-0.5, -sqrt(3.0) * 0.5)), 0.0) * vec2(-0.5, -sqrt(3.0) * 0.5);
    position = abs(position);
    position -= min(position.y - sqrt(3.0) * position.x, 0.0) * vec2(-sqrt(3.0) * 0.5, 0.5);
    position.y -= radius * 0.5;
    float d1 = length(position - vec2(sqrt(3.0) * 0.5 * radius, 0.0)) - radius;
    float d2 = length(position + vec2(sqrt(3.0) * 0.5 * radius, 0.0)) - radius;
    return max(d1, d2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float radius = 2.0 / 3.0 * 1.2;
    float d = flower(position, radius);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); 
    color *= 1.0 - exp(-4.0 * abs(d)); 
	color *= 0.8 + 0.2 * cos(120.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 3.0, (abs(d) - 0.002) * iResolution.y));
    if (iMouse.z > 0.0) {
        vec2 mousePosition = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
        float d = flower(mousePosition, radius);
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 3.0, abs(length(position - mousePosition) - abs(d)) * iResolution.y - 1.0));
    }
    fragColor = vec4(color, 1.0);
}
