Main {
   vec4 E = .25*(A(U+vec2(0,1))+A(U+vec2(1,1))+A(U+vec2(1,0))+A(U+vec2(0,0)));

    E *= 100.;

             Q = vec4(1)*(1.-exp(-.5*length(E.xy)))*
             smoothstep(2.*fwidth(12.*atan(E.x,E.y)),0.,abs(sin(12.*atan(E.x,E.y))));



    if (U.x<52.||U.y<52.||R.x-U.x<52.||R.y-U.y<52.)
        Q *= 0.;


    if (roundBox(U-vec2(.35,.5)*R,vec2(.04,.5)*R,3.)<0. || 
            roundBox(U-vec2(.65,.5)*R,vec2(.04,.5)*R,3.)<0.) 
   {

        Q = 10.*vec4(E.w,.25*abs(E.w),-E.w,1);

    }


}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define mu 1.
#define eps 1.
#define pi 3.141592659

#define K .5

float f (float x) {
    // Change the input signal here!
    return smoothstep(0.,1.,x)*sin(.5*x);
}


float roundBox ( vec2 p, vec2 b, float r) 
		{
		    vec2 h = 0.5*vec2(b.x,b.y);
		    vec2 q = p-clamp(p,-h+r,h-r);
		    if (length(q)>0.) return length(q) - r ;
            else return max(abs(p.x)-h.x,abs(p.y)-h.y);
		}

#define F 0
Main {
    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    if ((iFrame*4+F)%2==0) {
        // dE/dt = -curl B-j
        Q.x -= .5/eps*(n.z-s.z) + 0.;
        Q.y += .5/eps*(e.z-w.z) + Q.w ;
    } else {
        // dB/dt = curl E
        Q.z -= .5/mu*(n.x-s.x-e.y+w.y);
    }
    Q.w -= K*Q.y+(1.-K)*Q.w;

    if (roundBox(U-vec2(.35,.5)*R,vec2(.04,.5)*R,3.)<0.) Q.w += K*f((float(iFrame)/60.))/10.;
    else if (roundBox(U-vec2(.65,.5)*R,vec2(.04,.5)*R,3.)<0.) ;
    else Q.w = 0.;

    if (U.x<50.||U.y<50.||R.x-U.x<50.||R.y-U.y<50.)
        Q *= .98;
}

