void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 0.5+(fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 uv = 6.0*(coord-0.25*iTime);

	uv = sin(uv);
	uv = fract(uv);
	uv -= 0.5;

	float d = length(uv);
	float dc = length((coord-0.5)*2.0);
	float c = pow(smoothstep(1.0,0.2,dc),0.20);

	float d0 = length(coord-0.5);
	d0 = sin(d0*13.5);
	d0 = abs(d0);
	d0 = 1.0/d0;
	float t = smoothstep(0.3,0.9,d);

	vec4 color = vec4(0.0,1.0,2.0,1.0);
	color *= d0*t*c;

	fragColor = vec4 (color);
}
