float superLogicSnake(vec2 p, float s){
    float r = min(min(p.x,p.y),min(s-p.x-1.0,s-p.y-1.0));
    float h = float(p.y>p.x);
    float l = 4.0*(r+h)*(s-r-h);
    l += sign(0.5-h)*(p.x+p.y-2.0*r);
    return l;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord.x -= .5*(iResolution.x-iResolution.y);
    vec2 uv = fragCoord/iResolution.y;
    float xMask = 1.0-abs(floor(uv.x));
    float size = 2.0;
    float area = size*size;
    vec2 tilePos = uv*size;
    float flip = -1.0;
    float crazy = floor(log(mod(iTime*0.45,12.7)+1.0)*2.0);
    float snake1 = superLogicSnake(floor(tilePos),size);
    for( ; crazy > 0.0 ; crazy--){

        size = flip > 0.0 ? area-ceil(snake1)+2.0 : ceil(snake1)+2.0;

        flip *= -1.0;
        area = size*size;
        tilePos = fract(tilePos)*size;
        snake1 = superLogicSnake(floor(tilePos),size);
    }

    snake1 = mod(snake1+floor(iTime*15.0),area)/(area-1.0);
    vec3 col =vec3(snake1);
    col *= xMask;

fragColor = vec4(col,1.0);
}
