// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

uint uhash(ivec3 v)
{
    return triple32(uint(v.x) + triple32(uint(v.y) + triple32(uint(v.z))));
}
/*
vec3 barycentric(vec2 a, vec2 b, vec2 c, vec2 p)
{
    float d = (b.y - c.y) * (a.x - c.x) + (c.x - b.x) * (a.y - c.y);
    float alpha = ((b.y - c.y) * (p.x - c.x)+(c.x - b.x) * (p.y - c.y)) / d;
    float beta = ((c.y - a.y) * (p.x - c.x) + (a.x - c.x) * (p.y - c.y)) / d;
    float gamma = 1.0 - alpha - beta;
    return vec3(alpha, beta, gamma);
}
*/
vec3 barycentric(vec2 p)
{
    float s = sqrt(3.0);
    return vec3((vec2(-1,+1) * p.x - p.y/s + 1.0)/2.0, p.y / s);
}

float mirror(float t)
{
    return abs(2.0 * fract(t) - 1.0);
}

#define scale (10.0*sqrt(3.0))
//#define e (3./R.y*scale)
#define e (1./R.y*scale)

void mainImage( out vec4 o, in vec2 I )
{
    vec2 R = iResolution.xy;
    //I = 2.0 * I/R - 1.0;
    I = (2.0 * I - R) / R.y; // mind aspect ratio

    float h = sqrt(3.0);
    //vec3 t = barycentric(vec2(-1,0), vec2(+1,0), vec2(0,h), I*scale);
    vec3 t = barycentric(I*scale);

    ivec3 m = ivec3(floor(t));
    bool flip = m.x + m.y + m.z == 0;
    bool tri = any(lessThan(t, vec3(0)));
    t = fract(t);
    //t -= vec3(m);
    if(!flip) t = 1.0 - t;

    //uint choice = uhash(m / int(1.0+5.0*(0.5+0.5*cos(iTime))) + int(iTime)) % 3u;
    uint choice = uhash(m / int(1.0+5.0*mirror(iTime/5.0)) + int(iTime)) % 3u;
    t = choice == 2u ? t.zxy : choice == 1u ? t.yzx : t.xyz;

    float x = (t.y-t.x);
    //float c = 1.-smoothstep(0.1-e,0.1+e,abs(x))*smoothstep(0.1-e,0.1+e,abs(2.0*t.z-1.0));
    float c = 1.-min(max(smoothstep(0.1-e,0.1+e,abs(x)), smoothstep(0.1-e,0.1+e,2.0*t.z-1.0)), smoothstep(0.1-e,0.1+e,abs(2.0*t.z-1.0)));

    //o = vec4(t, 1.0);
    o = vec4(c);
    if(iMouse.z > 0.0)
    {
        o = max(o, vec4(0.2*(choice == 2u ? vec3(0,0,1) : choice == 1u ? vec3(0,1,0) : vec3(1,0,0)), 1.0));
        o += 0.5 - 0.5 * min(smoothstep(0.0,0.0+e,t.x), min(smoothstep(0.0,0.0+e,t.y), smoothstep(0.0,0.0+e,t.z)));
    }
    //if(tri) o.xyz = vec3(0,0,1); // fundamental triangle
    //if(flip) o.xyz = vec3(1,0,0);
    o = sqrt(o); // gamma
}
