/* (c) 2023 s.fx */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv from -1. to 1. (times 2) instead of 0. to 1.
    // and shifted to center (minus 1)
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;

    // correct ratio of x axis
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    if (length(uv) < .5)
        col = vec3(1.);

    fragColor = vec4(col,1.0);
}
