void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float fre;
    float numDis = 50.0;
    float width = 5.0;
    fre= width/(1.0 - floor(uv.x*numDis)/numDis);

    float func;
    vec2 pos = vec2(0, 50);
    vec2 dir = vec2(0.6, 0.8);
    func = dot((fragCoord-pos), dir);

    // func = func / T
    func = func / fre;
    func = func - floor(func);

    vec3 color;
    color = vec3(ceil(uv.x - func), ceil(uv.x - func), ceil(uv.x - func));





    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(color,1.0);
}
