// Finding circles by GeoffL in 2023-03-06
// https://www.shadertoy.com/view/msK3zz
// Based on the code here: https://www.shadertoy.com/view/4dcfW8

const float PI = acos(-1.);
const vec3 LINECOLOR = vec3(1.,1.,0./2.);
const float SCALE = 8., RAD = .95*SCALE;
float graphTHICK = sqrt(2.), T;

float S(float thick, float d){return smoothstep(thick, 0., d);} // antialiased draw. thick >= 1.5

float line(vec2 p, vec2 a, vec2 b) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    return length(p - b * h);
}

vec2 heart( float t ) { // https://mathcurve.com/courbes2d.gb/ornementales/ornementales.shtml
    float xt, yt;
  	xt = cos(t); yt = -sin(t) + sqrt(abs(cos(t))); // Jurjen Boss heart
  	// xt = pow(sin(t),3.); yt = cos(t) - pow(cos(t),4.); // RaphaÃ«l Laporte heart (1993)
	return vec2( xt, yt );
}

float parPlot(vec2 p){
    float d = 1e5;
  	float period = 2.*PI, step = 6.*period/360.;
    vec2 P=heart(0.);
    for (float t = step; t < period+step; t += step ) {
        d = min( d, line( p, P, P = heart(t) ) );
    }
  	return S(graphTHICK*(fwidth(p.x)+fwidth(p.y)), d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
    uv *= SCALE;
    T=iTime/4.;
    vec3 col = vec3(0.);
    float c, s, radius=6., steps = 12., th=(2.*PI/steps);
    mat2 r; vec2 m;
    for (float i=0.; i<steps; i++){
        c=cos(th*i+T); s=sin(th*i+T); r=mat2(c,-s,s,c);
        m = -radius*vec2(s,c);
        col += parPlot((uv + m)* r) * LINECOLOR;
    }

    fragColor = vec4( col , 1.0);
}
