const float gStep = .08;
const float gDisp = 3.8;
const float gAsym = .03;
const float gLayerShift = 0.5;
vec4 hash42(vec2 p)
{
	vec4 p4 = fract(p.xyxy * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 5.;
    float s = gLayerShift;
    for (float i = 0.;i<1.;i+=gStep)
    {
        s = -s;
        uv += sin(uv.yx*2.+iTime)*gAsym;
        uv += sin((uv.yx+iTime*.4)*gDisp)*-i*s;
        vec2 id = floor(uv);
        vec4 h = hash42(id);
        vec2 p = uv-id;
        vec2 d2 = min(p, 1.-p);
        float d = .6+min(d2.x,d2.y);
        o = (h * d*d);
    }
}
