#define CLAMP_FROM_RIGHT 0
#define CLAMP_FROM_LEFT 1
#define CLAMP_FROM_CENTER 2

const int clampDir = CLAMP_FROM_CENTER;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float progress = fract(iTime*0.5);


    // Output to screen
    if (clampDir == CLAMP_FROM_RIGHT) {
        uv.x = clamp(uv.x, 1.0 - progress, 1.0);
    } else if (clampDir == CLAMP_FROM_LEFT) {
        uv.x = clamp(uv.x, 0.0, progress);
    } else if (clampDir == CLAMP_FROM_CENTER) {
        float p = (1.0 - progress) / 2.0;
        uv.x = clamp(uv.x, p, 1.0 - p);
    }

    fragColor = texture(iChannel0, uv);
}
