void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(0.1);

    float t = iTime * 0.3;
    for (int i = 0; i < 7; i++)
    {
        vec2 newUV = uv;
        newUV.x += sin(t + float(i)*0.6) * 0.4;
        newUV.y += sin(t + float(i)*0.7) * 0.3;

        float dist = length(newUV);
        float intensity = (0.1/dist);
        color += intensity * vec3(cos(float(i) + t), sin(float(i) + t), sin(float(i) + t*1.5));
    }

    color = clamp(color, 0.0, 1.0);
    fragColor = vec4(color,2.0);
}
