/*
 * Trail Experiment (Main Image)
 * (c) 2023 s.fx
 * 
 * Main image shader which dithers buffer output
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv   = fragCoord / iResolution.xy;
    fragColor = vec4(texture(iChannel1, fragCoord / DITHER_SIZE).r < pow(texture(iChannel0, uv).r, EXPONENT));
}

/*
 * Trail Experiment (Buffer A)
 * (c) 2023 s.fx
 *
 * Buffer drawing pretty snake trail ~~o<
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = fragCoord / iResolution.xy;
    vec2 pos = vec2(.5) + vec2(sin(iTime), cos(iTime)) * .25 + vec2(sin(iTime * .1), cos(iTime * .2)) * .175;

    float prev = texture(iChannel0, uv).r;
    float rad  = sin(iTime * .5) * .5 + .55;        // variable radius scalar
    float len  = (sin(iTime * .2) * .4 + .6) * .01; // variable length of trail

    fragColor = vec4(length(pos - uv) < rad * RADIUS ? 1. : prev - len);
}
