void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize coordinates to the range [0, 1]
    vec2 uv = fragCoord/iResolution.xy;

    // Define a dark background color
    vec3 bgColor = vec3(0.05, 0.05, 0.1);

    // Create a moonlit effect
    vec3 moonColor = vec3(0.8, 0.8, 0.7);
    float moonRadius = 0.1;
    vec2 moonPosition = vec2(0.2, 0.8);
    float moonIntensity = smoothstep(moonRadius, moonRadius + 0.02, length(uv - moonPosition));

    // Combine background and moonlight
    vec3 finalColor = mix(bgColor, moonColor, moonIntensity);

    // Add a pseudo-3D effect using noise
    float depth = 1.0 - 2.0 * fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
    depth = abs(depth);

    // Apply shading based on depth
    float shading = smoothstep(0.2, 1.0, depth);
    finalColor *= shading;

    // Apply a vignette effect
    float vignette = smoothstep(0.8, 1.0, length(uv - 0.5));
    finalColor *= vignette;

    // Output final color
    fragColor = vec4(finalColor, 1.0);
}
