void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 originalcol = vec3(uv.x, uv.y, 0.5);

    //vec2 origin = vec2(0, 50);
    vec2 origin[3];
    origin[0] = vec2(0,50);
    origin[1] = vec2(1200,850);
    origin[2] = vec2(500,500);

    vec2 nvec[3];
    nvec[0] = vec2(0.6, 0.8);
    nvec[1] = vec2(0.8, 0.6);
    nvec[2] = vec2(-0.6, 0.8);


    float dis = 99999999999999.99;
    int closer = -1;
    float t0 = 20.0;
    float t1 = 20.0;
    float i_idx = 0.0;
    float j_idx = 0.0;
    int counts = 0;
    while (i_idx < 1.0){
        j_idx = 0.0;
        while (j_idx < 1.0){
            float x_idx = float(fragCoord.x) + i_idx;
            float y_idx = float(fragCoord.y) + j_idx;
            dis = 99999999999999.99;
            for (int k = 0; k<3; k++){
                float d = pow((pow(x_idx-origin[k].x,2.0)+pow(y_idx-origin[k].y,2.0)),0.5);
                if (d < dis){
                   dis = d;
                   closer = k;
                }
            }
            vec2 pminusp0 = vec2(x_idx-origin[closer].x, y_idx-origin[closer].y);
            float T = 10.0;

            float func = dot(nvec[closer],pminusp0)/T;
            func = func - floor(func);
            if (func < originalcol.x){
                counts ++;
            }
            j_idx += 1.0/t1;
        }
        i_idx += 1.0/t0;
    }

    vec3 finalcol = vec3(float(counts)/t0/t1, float(counts)/t0/t1, float(counts)/t0/t1);
    fragColor = vec4(finalcol,1.0);
}
