// CHANGE THIS PART TO SELECT IMAGE
#define iChannelIndex 0
#define iChannel iChannel0

// CLICK & MOVE HORIZONTAL MOUSE POINTER ON IMAGE

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0);

    int ci = iChannelIndex; 

float scale = floor(iMouse.x/100.0)+1.0;

// DEFAULT SCALING
if(iMouse.x == 0.0) scale = 2.0;

if (fragCoord.x < iChannelResolution[ci].x/scale) {
        // LEFT IMAGE
        fragCoord *= scale;
        float cnt = 0.0;

        for(float i = 0.0; i < scale; i++){
            for(float j = 0.0; j < scale; j++){
                col += texelFetch(iChannel , ivec2(fragCoord) + ivec2(i, j), 0).rgb; cnt++;
            }
        }

        col /= cnt;
    } else {
        // RIGHT IMAGE
        fragCoord.x -= iChannelResolution[1].x/scale;
        fragCoord *= scale;
        col  = texelFetch(iChannel , ivec2(fragCoord) + ivec2(0, 0), 0).rgb;
    }


    fragColor = vec4(col,1.0);
}
