void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec3 backgroundColor = vec3(0.0, 0.0, 0.0);

// Calculate the center point of the screen
vec2 center = vec2(iResolution.x / 2.0, iResolution.y / 2.0);

// Calculate the distance between the center point and the current fragment
float distance = length(center - fragCoord);

// Calculate the color of the fragment based on the distance from the center
vec3 fragmentColor = mix(vec3(1.0, 0.0, 0.0), backgroundColor, distance / center.x);

// Set the output color
fragColor = vec4(fragmentColor, 1.0);
}
