#define N 4

uint rand(uint x) {
    const uint k = 3282017729u;
    x *= k; x ^= k; x *= k; x ^= k;
    x *= k; x ^= k; x *= k; x ^= k;
    return x;
}
float u2f(uint x) {
    x >>= 9;
    x |= 0x3f800000u;
    return uintBitsToFloat(x)-1.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n[N];
    for (int i = 0;i < N;++i) {
        n[i] = u2f(rand((uint(i)*uint(iResolution.y)+uint(fragCoord.y))*uint(iResolution.x)+uint(fragCoord.x)));
    }
    vec4 t =texture(iChannel0, fragCoord/iResolution.xy);
    int mask = int((t.x+t.y+t.z)/3.0*float(N)*0.999999);
    fragColor = vec4(0);
    for (int i = 0;i < N;++i) {
        fragColor += vec4(n[i])*float(mask==i);
    }
    // Uncomment to see mix mask
    //fragColor = vec4(float(mask)/float(N));
}
