float plot(vec2 st, float pct){
    return smoothstep(pct-0.05, pct, st.y) - smoothstep(pct, pct+0.05, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // shaper function
    float y = 0.25 * (pow(abs(uv.x * 2.0) - 1.0, cos(iTime - 0.5) * 0.5 + 0.75) - cos(iTime + 0.5)) + 0.3;
    vec3 color = vec3(y);

    float pct = plot(vec2(uv.x,uv.y),y);
    float mask = float(uv.x > 0.25 && uv.x < 0.75);
    vec3 col = vec3(0.5,0.8,1.0) - pct * mask;
//   col = vec3(mask);

    // Output to screen
    fragColor = vec4(col,1.0);
}
