void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;
    vec3 c1 = texture(iChannel0, uv+vec2(1.0/iResolution.x, 0.0)).rgb;
    vec3 c2 = texture(iChannel0, uv+vec2(-1.0/iResolution.x, 0.0)).rgb;
    vec3 c3 = texture(iChannel0, uv+vec2(0.0, 1.0/iResolution.x)).rgb;
    vec3 c4 = texture(iChannel0, uv+vec2(0.0, -1.0/iResolution.x)).rgb;
    vec3 c5 = texture(iChannel0, uv+vec2(1.0/iResolution.x, 1.0/iResolution.x)).rgb;
    vec3 c6 = texture(iChannel0, uv+vec2(-1.0/iResolution.x, 1.0/iResolution.x)).rgb;
    vec3 c7 = texture(iChannel0, uv+vec2(-1.0/iResolution.x, 1.0/iResolution.x)).rgb;
    vec3 c8 = texture(iChannel0, uv+vec2(-1.0/iResolution.x, -1.0/iResolution.x)).rgb;
    float a, b, c, d, e, f, g, h;
    a = (c1.r + c1.g + c1.b)/3.0;
    b = (c2.r + c2.g + c2.b)/3.0;
    c = (c3.r + c3.g + c3.b)/3.0;
    d = (c4.r + c4.g + c4.b)/3.0;
    e = (c5.r + c5.g + c5.b)/3.0;
    f = (c6.r + c6.g + c6.b)/3.0;
    g = (c7.r + c7.g + c7.b)/3.0;
    h = (c8.r + c8.g + c8.b)/3.0;
    float cg = (col.r + col.g + col.b)/3.0;

    if((a+b+c+d+e+f+g+h)/8.0-cg >= 0.001) {
        col = vec3(sin(iTime*0.9)/2.0+0.5, cos(iTime*1.3)/2.0+0.5, sin(iTime*1.7)/2.0+0.5);
    }else {
        col = vec3(0.0);
    }
    // Output to screen
    fragColor = vec4(col, 1.0);
}
