#define sigma 1.0

#define PI 3.1415926535

float LoG(float x, float y) {
    float r2 = x*x+y*y;
    return (1.0/(PI*sigma*sigma*sigma*sigma))*(1.0-(r2/(2.0*sigma*sigma)))*exp(-r2/(2.0*sigma*sigma));
}

float g(float x) {
    return (1.0/(2.506628274631000502415765284811045253006986740609938316629923576*sigma))*exp(-(x*x)/(2.0*sigma*sigma));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(texture(iChannel0, fragCoord/iResolution.xy).rgb, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0);

    for(int i = -9; i <= 9; i++) {
        fragColor += g(float(i))*vec4(texelFetch(iChannel0, ivec2(fragCoord)+ivec2(i, 0), 0).rgb, 1.0);
    }

    fragColor = vec4(fragColor.a != 0.0 ? fragColor.rgb / fragColor.a : fragColor.rgb, 1.0);
}

