void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;
    vec4 solidRed = vec4(0,0.0,0.7,0.4);
    if((xy.x > 0.0) && (xy.x < 0.2)){
        solidRed.r = 0.2;
        if((xy.y > 0.2) && (xy.y < 0.4)){
            solidRed.r = 1.0;
        }    
        else if((xy.y > 0.6) && (xy.y < 0.8)){
            solidRed.r = 1.0;
        }
    }
    else if((xy.x > 0.2) && (xy.x < 0.4)){
        solidRed.r = 0.4;
        if((xy.y > 0.2) && (xy.y < 0.4)){
            solidRed.r = 1.0;
        }    
        else if((xy.y > 0.6) && (xy.y < 0.8)){
            solidRed.r = 1.0;
        }
    }
    else if((xy.x > 0.4) && (xy.x < 0.6)){
        solidRed.r = 0.2;
        if((xy.y > 0.2) && (xy.y < 0.4)){
            solidRed.r = 1.0;
        }    
        else if((xy.y > 0.6) && (xy.y < 0.8)){
            solidRed.r = 1.0;
        }
    }
    else if((xy.x > 0.6) && (xy.x < 0.8)){
        solidRed.r = 0.5;
        if((xy.y > 0.2) && (xy.y < 0.4)){
            solidRed.r = 1.0;
        }    
        else if((xy.y > 0.6) && (xy.y < 0.8)){
            solidRed.r = 1.0;
        }
    }
    else if(xy.x > 0.8){
        solidRed.r = 0.1;
        if((xy.y > 0.2) && (xy.y < 0.4)){
            solidRed.r = 1.0;
        }    
        else if((xy.y > 0.6) && (xy.y < 0.8)){
            solidRed.r = 1.0;
        }
    }

    fragColor = solidRed;

}
