const float PI = 3.1415926;
float atan2(vec2 xy){
    return xy.x == 0.0 ? sign(xy.y) * PI / 2.0 : atan(xy.y, xy.x);
}
vec2 xy2pol(vec2 xy){
    return vec2(atan2(xy), length(xy));
}
vec2 pol2xy(vec2 pol){
    return pol.y * vec2(cos(pol.x), sin(pol.y));
}

uvec4 k = uvec4(0x456789abu, 0x6789ab45u, 0x89ab4567u, 0xab456789u);
uvec4 u = uvec4(1, 2, 3, 4);
const uint UINT_MAX = 0xffffffffu;
uvec2 uhash22(uvec2 n){
    n ^= (n.yx << u.xy);
    n ^= (n.yx >> u.xy);
    n *= k.xy;
    n ^= (n.yx << u.xy);
    return n * k.xy;
}
float hash21(vec2 p){
    uvec2 n = floatBitsToUint(p);
    return float(uhash22(n).x) / float(UINT_MAX);
}
float vnoise21(vec2 p){
    vec2 n = floor(p);
    vec2 f = fract(p);
    float[4] v;
    for(int i = 0; i < 2; i++){
        for(int j = 0; j < 2; j++){
            v[i + 2 * j] = hash21(n + vec2(i, j));
        }
    }
    return mix(mix(v[0], v[1], f[0]), mix(v[2], v[3], f[0]), f[1]);
}
float gtable2(vec2 lattice, vec2 p){
    uvec2 n = floatBitsToUint(lattice);
    uint idx = uhash22(n).x >> 29;
    float u = 0.92387953 * (idx < 4u ? p.x : p.y);
    float v = 0.38268343 * (idx < 4u ? p.y : p.x);
    return((idx & 1u) == 0u ? u : -u) + ((idx & 2u) == 0u ? v : -v);
}
float pnoise21(vec2 p){
    vec2 n = floor(p);
    vec2 f = fract(p);
    float[4] v;
    for(int i = 0; i < 2; i++){
        for(int j = 0; j < 2; j++){
            v[i + j * 2] = gtable2(n + vec2(i, j), f - vec2(i, j));
        }
    }
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return 0.5 * mix(mix(v[0], v[1], f[0]), mix(v[2], v[3], f[0]), f[1]) + 0.5;
}
float fbm21(vec2 p, float g){
    float val = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 4; i++){
        val += amp * (vnoise21(freq * p) - 0.5);
        amp *= g;
        freq *= 2.01;
    }
    return 0.5 * val + 0.5;
}
float base21(vec2 p){
    return fbm21(p, 0.5);
}
float warp21(vec2 p, float g){
    float val = 0.0;
    for(int i = 0; i < 4; i++){
        val = base21(p + g * vec2(cos(2.0 * PI * val), sin(2.0 * PI * val)));
    }
    return val;
}
vec4 blend(float a, float b){
    float time = abs(mod(0.1 * iTime, 2.0) - 1.0);
    vec4[2] col2 = vec4[](
        vec4(a, a, 1, a),
        vec4(0, b, b, b)
    );
    return mix(col2[0], col2[1], smoothstep(0.5 - 0.5 * time, 0.5 + 0.5 * time, b / (a + b)));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = fragCoord / min(iResolution.x, iResolution.y);
    pos = 10.0 * pos + iTime;
    float a = warp21(pos, 0.5);
    float b = warp21(pos + 10.0, 2.0);
    fragColor = blend(a, b);
}
