const float PI = 3.141592; 
const float R = 1.;    // sphere radius
const float r = 0.09;  // circles radius
const float d = 0.1;   // circles distance
const vec3 a0 = vec3(0.); // starting angles (pitch, yaw, roll)
const vec3 speed = vec3(.5, .5, .5); // pitch, yaw, roll

vec3 pitch(vec3 uv, float rad){
    float c = cos(rad);
    float s = sin(rad);
    return vec3(uv.x,
                c*uv.y - s*uv.z,
                s*uv.y + c*uv.z);
}

vec3 yaw(vec3 uv, float rad){
    float c = cos(rad);
    float s = sin(rad);
    return vec3(c*uv.x + s*uv.z,
                uv.y,
               -s*uv.x + c*uv.z);
}

vec3 roll(vec3 uv, float rad){
    float c = cos(rad);
    float s = sin(rad);
    return vec3(c*uv.x - s*uv.y,
                s*uv.x + c*uv.y,
                uv.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float z = sqrt( max(0., R*R - (uv0.x)*(uv0.x) - (uv0.y)*(uv0.y)) ); 
    vec3 uv = vec3(uv0, z); // virtually creating a Z axis

    vec3 angle = a0 + vec3(speed * iTime);
    vec3 uv_r = vec3(pitch(uv, angle.x));
    vec3 uv_g = vec3(  yaw(uv, angle.y));
    vec3 uv_b = vec3( roll(uv, angle.z));

    float lr = length(mod(uv_r + r, 2.*d) - r);
    float lg = length(mod(uv_g + r, 2.*d) - r);
    float lb = length(mod(uv_b + r, 2.*d) - r);
    vec3 l = vec3(lr, lg, lb);

    vec3 pixel = smoothstep(3./iResolution.y, 0., l-r) * smoothstep(0., 3./iResolution.y, R - length(uv.xy) );
    fragColor = vec4(pixel, 1.);
}
