float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
vec3 pal(float t) {
    vec3 a = vec3(0.248, 0.138, 0.500);
    vec3 b = vec3(-0.172, 0.058, 0.338);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(-0.442, -1.002, 0.058);

    return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    uv = fract(uv*2.0) - 0.5;

    float d = sdBox(uv, vec2(0.3, 0.3));
    vec3 col = pal(d + iTime);

    d = sin(d*80.0 + iTime/2.0)/80.0;
    //d = 0.001 / d;
    d = (cos(iTime)+1.0)/1000.0 / d;

    col *= d;

    fragColor = vec4(col, 1.0);
}
