float Circle(vec2 uv, vec2 pos, float r, float blur) 
{
    float d = length(uv - pos);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Pulse(float amount, float speed, float offset)
{
    float time = iTime + offset;
    float p = sin(time * speed) * amount;
    return abs(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Setting up uv
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    //Drawing circles
    vec3 col = vec3(0.);

    float mask = Circle(uv, vec2(0., 0.0),.4, .05);

    float eye1 = Circle(uv, vec2(.1, .1), .05, .005);
    float eye2 = Circle(uv, vec2(-.1, .1), .05, .005);

    eye1 -= Circle(uv, vec2(.1, .1), Pulse(0.04, 5., 0.), .005);
    eye2 -= Circle(uv, vec2(-.1, .1), Pulse(0.04, 5., 0.5), .005);

    float mouth = Circle(uv, vec2(0., 0.),.3, .005);
    mouth -= Circle(uv, vec2(0., 0.1), Pulse(0.05, 5., 5.) + 0.3, .005);

    mask -= eye1 + eye2 + mouth;

    col = vec3(1., 0., 0.) * mask;
    //Display
    fragColor = vec4(col, 1.);
}
