float vabs(vec2 v)
{
    return sqrt(v.x * v.x + v.y * v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float t = iTime;

    float r = tan(pow(uv.y + t, uv.x));
    float g = tan(pow(vabs(uv) + t, vabs(uv)));
    float b = tan(pow(uv.x + t, uv.y));

    vec3 col = vec3(r, g, b);

    fragColor = vec4(col,1.0);
}
