void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    uv *= 5.0;
    uv.x -= 1.0;
    // Time varying pixel color
    vec3 col = vec3(
        0.7 * sin(2.0 * (uv.x + 0.2) + 0.7 * iTime) + 1.3 * sin( 1.2 * (uv.y - 2.3) - 0.4 * iTime) - 1.4 * cos(0.7 * (uv.x + 0.3) * (uv.y + 1.4) + 4.0 * iTime) + 2.0,
        cos(1.2 * (uv.x - 1.4) + 2.2 * iTime) + 1.1 * cos(-1.3 * (uv.y + 1.7) - 1.4 * iTime) - 2.2 * sin(0.3 * (uv.x + 2.3) * (uv.y + 0.9) - 1.8 * iTime) + 2.3,
        cos(1.7 * (uv.x - 2.3) + 1.3 * iTime) + 2.4 * sin( 0.4 * (uv.y - 2.3) - 0.6 * iTime) - 2.1 * cos(0.43 * (uv.x - 2.1) * (uv.y - 2.2) + 2.7 * iTime) + 2.4

    ) / 5.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
