void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizes the coordinates to the range [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    float intensity = sqrt(uv.x * uv.x + uv.y * uv.y);

    // Use sqrtU and sqrtV to define a color
    // Here, we use sqrtU for red, sqrtV for green, and their average for blue
    vec3 color = vec3(intensity, intensity, intensity);

    // Set the color
    fragColor = vec4(color, 1.0);
}
