#define s 1000.
#define d 50.
void mainImage( out vec4 O, in vec2 F )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 i = 1./iResolution.xy;
    F*=i;

    // Time varying pixel color
    vec4 c = vec4(0);
    for (float n=0.;n<s;n++)
    {
        vec2 bs = (vec2(sin(n*137.508),cos(n*137.508))*sqrt(n)*d*i)/sqrt(s);
        c+=texture(iChannel0,F+bs);   
    }

    // Output to screen
    O = c/s;
}
