void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // 规范化像素坐标（从 0 到 1）
    vec2 uv = fragCoord / iResolution.xy;

    // 将坐标中心移动到屏幕中心
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // 当前时间
    float time = iTime;

    // 使用 distance 和 sin 函数创建波纹
    float distanceFromCenter = distance(uv, vec2(0.0, 0.0));
    float wave = sin(distanceFromCenter * 10.0 - time * 2.0);

    // 根据波纹和时间改变颜色
    vec3 color = 0.5 + 0.5 * cos(time + uv.xyx + vec3(0, 2, 4));

    // 将波纹效果融入颜色中
    color *= wave;

    // 输出最终颜色
    fragColor = vec4(color, 1.0);
}
