#define S(a, b, c) smoothstep(a, b, c)

float smoke(vec2 uv, float time, float light, int o){
    float t;
    vec2 p = uv*10.;
    float c =0.;
    float x;

    for(int i = 0; i < 6; i++){
       t = iTime*time * float(i+1);
       x += cos(t-p.y)+sin(t+p.x);
        float y = sin(p.x);

        vec2 of = vec2(x,y);
        p += of;

       c += length(p);
    }
    return light/c;
}

float cube(vec2 uv, float l, float r, float u, float d, float iL, float iR, float bl){


    float left = S(uv.y*iL+l-bl,uv.y*iL+l+bl, uv.x);
    float right = S(uv.y*iR+r-bl, uv.y*iR+r+bl, uv.x);
    float up = S(u+bl,u-bl, uv.y);
    float down = S(d-bl,d+bl, uv.y);

    float c = (left - right) * up * down;

    return c;

}

float letter(vec2 uv){

    float stick = cube(uv, -.065, 0.075, 0.79, 0.07, 0., 0., 0.003); 
    float base = cube(uv, -.175, .25, .155, .064, .7, 1., 0.003);
    float top = cube(uv, .435, 0.07, .795, .6, -.7, 0., 0.003);

    return min(top+stick+base, 1.);

}

float letterOut(vec2 uv){

    float stick = cube(uv, -.12, 0.13, 0.85, 0.04, 0., 0., 0.003); 
    float base = cube(uv, -.25, .32, .21, .02, .7, 1., 0.003);
    float top = cube(uv, .375, 0.09, .85, .6, -.7, 0., 0.003);

    return min(top+stick+base, 1.);

}

float letterLight(vec2 uv){

    float stick = cube(uv, -.08, 0.093, 0.806, 0.07, 0., 0., 0.003); 
    float base = cube(uv, -.2, .27, .17, .05, .7, 1., 0.003);
    float top = cube(uv, .415, 0.09, .81, .6, -.7, 0., 0.003);

    return min(top+stick+base, 1.);

}

vec3 Letter(vec2 uv, vec3 col){

    float letBase = letterOut(uv);
    float letIn = letter(uv);
    float letLight = letterLight(uv);

    float apBase = S(2., 4., iTime);
    float apLight = S(3.,6., iTime);
    float apIn = S(6., 8., iTime);

    float edge = smoke(uv+vec2(-0.4,.1), 0., 20., 6);
    float edgeOut = smoke(uv, .05, 20., 6);

    col = mix(col, vec3(0), letBase*apBase);
    col = mix(col, vec3(0.995,0.671,0.129)*edgeOut, letLight*apLight);
    col = mix(col, vec3(0.995,0.671,0.129)*edge, letIn*apIn);

    return col;

}

vec3 Edge(vec2 uv, vec3 col){
    vec2 m = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    float t = iTime*.1;
    vec3 colorSmoke = vec3(0.,0.45,0.6);
    vec3 colorEdge = vec3(0.995,0.671,0.129);

    float interaction = length(m)<.35 ? 3. : 1.;
    float velocity = length(m)<.35 ? 2.5 : 1.;
    float animationIntro = min(1., S(0., 2., iTime));
    float animationBackground = min(1., S(4., 7., iTime));


    float circle = length(uv);
    float circleBlack = smoothstep(0.365, 0.354, circle) - smoothstep(0.34, 0.33, circle);
    float circleBlackOut = smoothstep(0.44, 0.43, circle) - smoothstep(0.4, 0.39, circle);
    float smok = smoke(uv, 0.2*velocity, 5.*interaction, 6);
    float edge = smoke(uv, 0., 15., 6);
    float textOut = smoke(uv/7.+vec2(-0.49, -0.1), .025, 3., 1);

    col = mix(col, colorEdge*edge, smoothstep(0.4, 0.39,circle));
    col = mix(col, smok*colorSmoke, smoothstep(0.36, 0.35, circle));
    col = mix(col, vec3(0),circleBlack);
    col = mix(vec3(0), col, smoothstep(0.4, 0.39, circle)*animationIntro);
    col = mix(col,vec3(0,.6,1.9)*textOut,(1.-smoothstep(0.4, 0.39, circle))*animationBackground);
    col = mix(col, vec3(0), circleBlackOut*animationIntro);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    uv*=1.1;

    col = Edge(uv, col);
    col = Letter((uv*.9-vec2(-0.14, -0.4)), col);
    fragColor = vec4(col,1.0);
}
