// With FabriceNeyret2: 195 chars
#define S(v) smoothstep( 0., -7./R.y , v)
void mainImage( out vec4 o, vec2 i )
{
    vec2 R = iResolution.xy,
         U = (i+i-R)/R.y;
    o = vec4(1, .28, .36, 1) *
        max(
            S( abs( length(U)-.825 ) - .125 ),
            U.y > -.72 ? S( .7*U.y + abs(U.x) - .1 ) : 0.
        );
}

