void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;;

    vec3 finalColor = vec3(0);
    vec3 color = vec3(0.);

    float amplitude = .6 * cos(uv.x  * 1.5 + 3.14159265359/2.);


    float curve =  amplitude * sin(uv.x * 50. + 3.14159265359 * iTime);

    curve = clamp(distance(curve, uv.y), -1., 1.);

    curve = smoothstep(curve, .1, 1.);


    finalColor += curve;
    fragColor = vec4(finalColor, 1.);
}
