#define res iResolution.xy
#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define Dir(a) vec2(cos(a),sin(a))
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define ss(a) smoothstep(k, -k, a)
#define eps 1e-4

// https://iquilezles.org/articles/distfunctions2d/
float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 O, in vec2 px )
{
    float k = 1. / res.y;
    vec2 uv = (px - 0.5 * res) * k;
    vec2 ouv = uv;

    float t = 1.8 * iTime;   
    uv *= rot(0.06*pi*cos(t));

    float r = 0.2 - 0.02 * cos(0.5*t);
    uv.y += r;

    vec2 pHead = vec2(0, mix(1.2, 1.5, .5-.5*cos(2.*t)) * r);

    float a = acos(pHead.y / r - 1.);  
    float theta = (a-eps)*thc(2., 0.5 * uv.x + t) - pi/2.;
    vec2 pHip = pHead + r * Dir(theta);
    pHip.y += (r-pHip.y) * sin(2.*t);
    vec2 pLegL = vec2(pHip.x + sqrt(r*r-pHip.y*pHip.y), 0);
    vec2 pLegR = vec2(pHip.x - sqrt(r*r-pHip.y*pHip.y), 0);

    vec2 dir = normalize(pHead-pHip);
    float a2 = atan(dir.y, dir.x);
    pHead = pHip + r * dir;
    vec2 pBody = mix(pHead, pHip, 0.3);
    vec2 pArmL = pBody - 0.65 * r * Dir(a2 - pi/2. - sin(1.5*t) + .1*cos(2.*t));
    vec2 pArmR = pBody + 0.65 * r * Dir(a2 - pi/2. - sin(1.5*t) - .1*cos(2.*t));
    vec2 pFloorL = vec2(-2. * r, -0.02);
    vec2 pFloorR = vec2( 2. * r, -0.02);

    float dHead     = length(uv - pHead)        - 0.03;
    float dHeadHip  = seg(uv, pHead, pHip)      - 0.01;
    float dHipLegL  = seg(uv, pHip, pLegL)      - 0.01;
    float dHipLegR  = seg(uv, pHip, pLegR)      - 0.01;
    float dBodyArmL = seg(uv, pBody, pArmL)     - 0.01;
    float dBodyArmR = seg(uv, pBody, pArmR)     - 0.01;
    float dFloor    = seg(uv, pFloorL, pFloorR) - 0.01;
    float d = min(min(dHead, dHeadHip), min(dHipLegL, dHipLegR));    
    d = min(min(d, dFloor), min(dBodyArmL, dBodyArmR));

    float dCircle = length(ouv) - (0.23 - 0.231 * thc(5., 0.5 * t));
    float sCircle = ss(-dCircle);

    float xp = exp(-20.*sqrt(abs(d)));
    float s = max(ss(d), xp);
    s = 1.- abs(s - sCircle);
    vec3 col = vec3(1.-s);
    vec3 tx1 = texture(iChannel1, ouv).rgb;
    vec3 tx2 = texture(iChannel2, vec3(ouv, exp(-0.5*s*s))).rgb;

    col = mix(2.*(1.-tx2)*sqrt(s)*tx1, col, 1.-s*s);
    O = vec4(col, 1.0);
}
