const float PI = 3.14;

vec3 palette( in float t )
{
    vec3 a = vec3(0.821,0.328,0.242);
    vec3 b = vec3(0.659,0.481,0.896);
    vec3 c = vec3(0.612,0.340,0.296);
    vec3 d = vec3(2.820,3.026,-0.273);
    return a + b*cos(6.28318*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 finalColor = vec3(0.0, 0.0, 0.0);  

    float d = fract(length(uv * 4.0)) / 4.;

    vec3 c = palette(length(uv) + iTime * 0.4);
    d = sin(8.0 * d + iTime) / 8.0;
    finalColor += c * pow(d, 0.8) * 1.8;

    fragColor = vec4(finalColor, 1.0);
}
