//Made as in https://upload.wikimedia.org/wikipedia/commons/c/c6/Bilininterp.png

float Lerp(float x, float q1, float q2)
{
    float q = q1 + x*(q2-q1);
    return q;
}

vec3 Rainbow(float h)
{
    const float PI = 3.14159265359;
    h = clamp(h, 0.0, 1.0);
    h *= 2.0/3.0;

    float r = (sin((h+1.0/3.0)*2.0*PI + PI/2.0)+1.0)/2.0;
    float g = (sin((h+2.0/3.0)*2.0*PI + PI/2.0)+1.0)/2.0;
    float b = (sin(h*2.0*PI + PI/2.0)+1.0)/2.0;

    vec3 col = vec3(r, g, b);
    col = round(col*20.0)/20.0;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0, 0.0, 0.0);

    float q1 = 0.0;
    float q2 = 1.2;
    float q3 = 1.2;
    float q4 = 0.5;

    float heigh1 = Lerp(uv.x, q1, q2);
    float heigh2 = Lerp(uv.x, q3, q4);
    float heigh = Lerp(uv.y, heigh1, heigh2);

    col = Rainbow(heigh);

    // Output to screen
    fragColor = vec4(col,1.0);
}
