void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 t = texture(iChannel0,uv.xy);




    // Output to screen
    fragColor = vec4(t.xyz,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float d = length(fragCoord.xy-iMouse.xy)/100.;
    //float m = 
    d /= 1.-float(iMouse.z < 0.);

    float s = 0.;

    fragCoord = fragCoord.xy / iResolution.xy;
    float self = texture(iChannel0,fragCoord).w;
    vec2 r = iResolution.xy /16.;
    //r.x = round(r.x);
    //r.y = round(r.y);
    float a = 1.0;
    float b = 1.0;

    //s += texture(iChannel0,fragCoord+vec2( 1, 0)/ r /b).w;
    //s += texture(iChannel0,fragCoord+vec2( 1, 1)/ r /a).w;
    //s += texture(iChannel0,fragCoord+vec2( 1,-1)/ r /a).w;
    //s += texture(iChannel0,fragCoord+vec2(-1, 0)/ r /b).w;
    //s += texture(iChannel0,fragCoord+vec2(-1, 1)/ r /a).w;
    //s += texture(iChannel0,fragCoord+vec2(-1,-1)/ r /a).w;
    //s += texture(iChannel0,fragCoord+vec2( 0,-1)/ r /b).w;
    //s += texture(iChannel0,fragCoord+vec2( 0, 1)/ r /b).w;
    float pi = 3.141569;
    float n = 8.;
    for (float i = 0.; i<(n); i ++){
    float an = i*pi/n*2.;
    s += texture(iChannel0,fragCoord+vec2( cos(an), sin(an))/ r ).w;

    }


    s = round(s+.5);
    float z = self+0.;

    z += float ( s == 1. );

    //z += float ( s == 4. );

    //z += (float (s == 2.))*(float (self > 0.));


    z -= float (s < 1.)*2.;
    z -= float (s > 1.)*2.;

    z += float(d<.5*5.)*1.;
    z += float(d<.25)*1.;
    z += float(d<.125)*1.;
    z += float(d<.125/2.)*1.;
    z = min(max(z,0.),1.);


    fragColor = vec4(abs(self-z),mod(s,1.),min(max(z,0.),1.),min(max(z,0.),1.));

}
