#define H(v)     fract(1e3*sin(dot(v,vec2(73,91))))                           // hash
#define D(C,x,y) O = mix(O, C, smoothstep( .01,0.,length(U-vec2(x,y))-.05 ) ) // dot of col C at (x,y)

void mainImage( out vec4 O, vec2 u )
{
    u /= 128.; u += iTime;
    int  h = int(8.*H(floor(u)));                            // rand in {0,...7}
    O = .8 + .3 * cos( 6.3*float(h)/8.  + vec4(0,23,21,0));  // desaturated hue(rand)
    vec2 U = fract(u);                                       // local frame
    if ( h/4   > 0 ) U = U.yx;                               // rand bit3: symmetry
    if ( h/2%2 > 0 ) U = 1.-U;                               // rand bit2: rot Pi
    if ( h%2   > 0 ) U = vec2(1.-U.y,U);                     // rand bit1: rot Pi/2
 // O = vec4(U,0,0);                                         // draw local parameterization
    O *= texture(iChannel0,U);                               // draw texture in tile
    D( vec4(1,0,0,0), .9,.5 );                               // +X: red dot
    D( vec4(0,1,0,0), .5,.9 );                               // +Y: greed dot
}
