#define t iTime
#define r iResolution.xy

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 c = vec3(0.0);
    float l, z = t;
    for (int i = 0; i < 3; i++) {
        vec2 uv, p = fragCoord.xy / r;
        uv = p;
        p.x *= r.x / r.y;
        p -= vec2(0.5, 1.1);
        z += 0.05;
        l = length(p);

        float grid = 0.7 * sin(p.x * 20.0 + z) * cos(p.y * 20.0 + z + 25.);

        float wave = (sin(z) + 1.) * abs(sin(l * 7. - z * 1.));

        uv += (p / l) * wave * grid;
        uv = mod(uv, 1.0);

        float angle = atan(p.y, p.x);
        float radius = length(p) * 2.;
        uv += vec2(cos(angle * radius - z), sin(angle * radius - z)) * grid;

        c[i] = 0.03 / length(mod(uv, 1.) - 0.5);
    }

    c = c / (l + 0.3);

    fragColor = vec4(c, 1.0);
}
